package Strings;

public class string_adv_fun {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String husbend = "venu";
		String wife = "divya";
		System.out.println(husbend.concat(wife));
		String buddi = "puli";
		System.out.println(buddi);
		System.out.println(husbend.replaceAll(wife, buddi));

	}

}
